mapper.select:

        where d.del_flag = '0'

			AND dept_id = #{deptId}


			AND parent_id = #{parentId}


			AND dept_name like concat('%', #{deptName}, '%')



			AND dept_type = #{deptType}


			AND status = #{status}


		${params.dataScope}
		order by d.parent_id, d.order_num

SELECT d.dept_id, d.parent_id, d.ancestors, d.dept_name, d.dept_type, d.order_num, d.leader, d.phone, d.credit_code, d.district_code, d.detailed_address, d.legal_name, d.legal_code, d.charge_name, d.charge_code, d.license_url, d.STATUS, d.del_flag, d.create_by, d.create_time FROM sys_dept d WHERE d.del_flag = '0'
AND dept_id = #{deptId}
AND ( d.dept_id IN ( SELECT dept_id FROM sys_dept WHERE dept_id = #{parentId} OR find_in_set( #{parentId}, ancestors ) ) )
AND dept_name like concat('%', #{deptName}, '%')
AND dept_type = #{deptType}
AND status = #{status}
order by d.parent_id, d.order_num


        where d.del_flag = '0'

			AND parent_id = #{parentId}


			AND dept_name like concat('%', #{deptName}, '%')


			AND dept_type = #{deptType}


			AND find_in_set(#{deptId}, ancestors)

select d.dept_id from sys_dept d left join sys_role_dept rd on d.dept_id = rd.dept_id where rd.role_id = #{roleId}
and d.dept_id not in (select d.parent_id from sys_dept d inner join sys_role_dept rd on d.dept_id = rd.dept_id and rd.role_id = #{roleId})
order by d.parent_id, d.order_num

select * from
	(select  u.user_id as id, u.dept_id as parent_id, u.nick_name as label ,1 as level
	 from sys_user u left join sys_dept d on u.dept_id = d.dept_id
	 where u.del_flag = '0' ${params.dataScope}
	)tempu
union
select  * from
	(
		select d.dept_id as id, d.parent_id, d.dept_name as label, 0 as level
		from sys_user u
				 left join sys_dept d on u.dept_id = d.dept_id
		where u.del_flag = '0' ${params.dataScope}
	)tempd

mapper.update:
update sys_dept
parent_id = #{parentId}, dept_name = #{deptName}, dept_type = #{deptType}, ancestors = #{ancestors}, order_num = #{orderNum}, leader = #{leader}, phone = #{phone}, credit_code = #{creditCode}, district_code = #{districtCode}, detailed_address = #{detailedAddress}, legal_name = #{legalName}, legal_code = #{legalCode}, charge_name = #{chargeName}, charge_code = #{chargeCode}, license_url = #{licenseUrl}, status = #{status}, update_by = #{updateBy}, update_time = sysdate()
where dept_id = #{deptId}

update sys_dept set ancestors =

    when #{item.deptId} then #{item.ancestors}

where dept_id in

    #{item.deptId}
